let number = document.querySelectorAll(".number"), view = document.querySelector(".output"), operation = document.querySelectorAll(".operation"); const power = document.querySelector("[data-power]"), sqrt = document.querySelector("[data-sqrt]"), equals = document.getElementById("equals"), clearBtn = document.querySelector(".clear"), del = document.querySelector(".delete"); function calc(domElement) { domElement.addEventListener("click", e => { let valueBtn = e.target.textContent, res = view.textContent + valueBtn; switch (view.textContent = res, valueBtn) { case "+": res += res; break; case "-": res -= res; break; case "*": res *= res; break; case "/": res /= res; break }equals.addEventListener("click", () => { let total = +eval(res).toFixed(2); view.textContent = total }) }) } function delLastElement() { del.addEventListener("click", () => { let e = view.textContent; view.textContent = e.slice(0, -1) }) } function elevate() { power.addEventListener("click", () => { let e = view.textContent; "" !== view.textContent && (view.textContent = Math.pow(+e, 2)) }) } function squareRoot() { sqrt.addEventListener("click", () => { let e = view.textContent; "" !== view.textContent && (view.textContent = Math.sqrt(+e)) }) } function clear() { clearBtn.addEventListener("click", () => { "" !== view.textContent && (view.textContent = "") }) } !function () { for (let e = 0; e < number.length; e++)calc(number[e]); for (let e = 0; e < operation.length; e++)calc(operation[e]) }(), delLastElement(), elevate(), squareRoot(), clear();